import styled from "styled-components";

export const Btn = styled.div`
  background-image: linear-gradient(to right, #005c97, #363795);
  background-size: 200%;
  color: white;
  padding: 0.5rem 0;
  border-radius: 0.5rem;
  box-shadow: 0 1rem 1.5rem rgba(33, 33, 33, 0.2);
`;
