import React from "react";
import { Container, Bottom, Message, Fixed, MessageWrapper } from "./styles";
import {
  FaLinkedin,
  FaGithub,
  FaRegEnvelope,
  FaChevronUp
} from "react-icons/fa";
import IconWrapper from "../IconWrapper";
import { useState, useEffect, useRef, useContext } from "react";
import { Context } from "../../context";

export default function Sidebar(props) {
  const [showArrow, setShowArrow] = useState(false);
  const [showMessage, setShowMessage] = useState(false);
  const firstUpdate = useRef(true);
  const context = useContext(Context);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => window.removeEventListener("scroll", handleScroll);
  });

  useEffect(() => {
    if (firstUpdate.current || showMessage === false) {
      firstUpdate.current = false;
      return;
    }
    setTimeout(() => setShowMessage(false), 1000);
  }, [showMessage]);

  function handleScroll() {
    const y = window.scrollY;
    if (y === 0 && showArrow) setShowArrow(false);
    else if (y > 0 && !showArrow) setShowArrow(true);
  }

  function handleArrowClick() {
    window.scrollTo(0, 0);
  }

  function handleClickEmail() {
    const textarea = document.createElement("textarea");
    textarea.style.opacity = 0;
    textarea.innerText = "fabiowabreu@gmail.com";
    document.documentElement.append(textarea);
    textarea.select();
    textarea.setSelectionRange(0, 99999);
    document.execCommand("copy");
    setShowMessage(true);

    document.documentElement.removeChild(textarea);
  }

  return (
    <Container>
      <Fixed>
        <IconWrapper>
          <a
            target="_blank"
            href="https://www.linkedin.com/in/f%C3%A1bio-de-abreu-371303159/"
            rel="noopener noreferrer"
          >
            <FaLinkedin />
          </a>
        </IconWrapper>
        <IconWrapper>
          <a
            target="_blank"
            href="https://github.com/Abreu00"
            rel="noopener noreferrer"
          >
            <FaGithub />
          </a>
        </IconWrapper>
        <IconWrapper>
          <MessageWrapper onClick={handleClickEmail}>
            <FaRegEnvelope />
            <Message show={showMessage}>{context.translation.copyMsg}</Message>
          </MessageWrapper>
        </IconWrapper>
        <Bottom showArrow={showArrow}>
          <div onClick={handleArrowClick}>
            <IconWrapper>
              <FaChevronUp />
            </IconWrapper>
          </div>
        </Bottom>
      </Fixed>
    </Container>
  );
}
